const chatId = $('Extract Message').first().json.chat_id; 
const messageText = $('Extract Message').first().json.message_text; 
const userId = $('Extract Message').first().json.user_id; 
const knowledge = $('Get Knowledge Base').first().json; 

// === –ß–ê–°–¢–ò –ü–†–û–ú–ü–¢–ê ===
const promptIntro = `–¢—ã ‚Äî –ò–ò-–∫–æ–Ω—Å—É–ª—å—Ç–∞–Ω—Ç –º–∞–≥–∞–∑–∏–Ω–∞ –∫–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω–æ–≥–æ –º–µ—Ä—á–∞ –∫–æ–º–ø–∞–Ω–∏–∏ TechFlow Solutions.`;
const promptCatalog = `\n\n**–ö–∞—Ç–∞–ª–æ–≥ —Ç–æ–≤–∞—Ä–æ–≤:**\n${JSON.stringify(knowledge.products, null, 2)}`;
const promptFAQ = `\n\n**FAQ:**\n${JSON.stringify(knowledge.faq, null, 2)}`;
const promptInstructions = `\n\n**–ò–Ω—Å—Ç—Ä—É–∫—Ü–∏–∏:**
1. –û—Ç–≤–µ—á–∞–π –Ω–∞ –≤–æ–ø—Ä–æ—Å—ã –æ —Ç–æ–≤–∞—Ä–∞—Ö –∏–∑ –∫–∞—Ç–∞–ª–æ–≥–∞
2. –ù–∞ –≤–æ–ø—Ä–æ—Å—ã –æ–± –æ–ø–ª–∞—Ç–µ, –¥–æ—Å—Ç–∞–≤–∫–µ, –≤–æ–∑–≤—Ä–∞—Ç–µ –æ—Ç–≤–µ—á–∞–π –ø–æ FAQ
3. –ò—Å–ø–æ–ª—å–∑—É–π —Ñ—É–Ω–∫—Ü–∏–∏ –¥–ª—è —Ä–∞–±–æ—Ç—ã —Å –∫–æ—Ä–∑–∏–Ω–æ–π
4. –ü—Ä–∏ –ø–µ—Ä–≤–æ–º –∫–æ–Ω—Ç–∞–∫—Ç–µ –ø—Ä–µ–¥—Å—Ç–∞–≤—å—Å—è –∏ —Ä–∞—Å—Å–∫–∞–∂–∏ —á—Ç–æ —Ç—ã —É–º–µ–µ—à—å 
5. –ù–µ –≤—ã–¥–∞–≤–∞–π –≤–µ—Å—å —Å–ø–∏—Å–æ–∫ —Ç–æ–≤–∞—Ä–∞ - —Å–Ω–∞—á–∞–ª–∞ —Å–æ–æ–±—â–∏ –∫–∞–∫–∏–µ –µ—Å—Ç—å –≥—Ä—É–ø–ø—ã, –∞ –ø–æ—Ç–æ–º –ø–æ—Å–ª–µ –≤—ã–±–æ—Ä–∞ –≥—Ä—É–ø–ø—ã –º–æ–∂–Ω–æ –ø–æ–∫–∞–∑–∞—Ç—å —Å–ø–∏—Å–æ–∫ —Ç–æ–≤–∞—Ä–æ–≤ –≤ –≥—Ä—É–ø–ø–µ
6. –ë—É–¥—å –≤–µ–∂–ª–∏–≤—ã–º –∏ –∫—Ä–∞—Ç–∫–∏–º
7. –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –∫–æ—Ç–æ—Ä–æ–∞—è –Ω–µ –Ω—É–∂–Ω–∞ –≤ –ø—Ä–æ—Ü–µ—Å—Å–µ –ø—Ä–æ–¥–∞–∂–∏ –∏–≥–Ω–æ—Ä–∏—Ä—É–µ—Ç—Å—è`;

const systemPrompt = promptIntro + promptCatalog + promptFAQ + promptInstructions;

const tools = [
  {type: 'function', function: {name: 'add_item', description: '–î–æ–±–∞–≤–∏—Ç—å —Ç–æ–≤–∞—Ä –≤ –∫–æ—Ä–∑–∏–Ω—É', parameters: {type: 'object', properties: {product_id: {type: 'string'}, qty: {type: 'integer', minimum: 1}}, required: ['product_id', 'qty']}}}, 
  {type: 'function', function: {name: 'remove_item', description: '–£–¥–∞–ª–∏—Ç—å —Ç–æ–≤–∞—Ä', parameters: {type: 'object', properties: {product_id: {type: 'string'}}, required: ['product_id']}}}, 
  {type: 'function', function: {name: 'get_cart', description: '–ü–æ–∫–∞–∑–∞—Ç—å –∫–æ—Ä–∑–∏–Ω—É', parameters: {type: 'object', properties: {}}}}, 
  {type: 'function', function: {name: 'confirm_order', description: '–û—Ñ–æ—Ä–º–∏—Ç—å –∑–∞–∫–∞–∑', parameters: {type: 'object', properties: {phone: {type: 'string'}, address: {type: 'string'}}, required: ['phone', 'address']}}}, 
  {type: 'function', function: {name: 'cancel_cart', description: '–û—á–∏—Å—Ç–∏—Ç—å –∫–æ—Ä–∑–∏–Ω—É', parameters: {type: 'object', properties: {}}}}, 
  {type: 'function', function: {name: 'show_product_photo', description: '–ü–æ–∫–∞–∑–∞—Ç—å —Ñ–æ—Ç–æ —Ç–æ–≤–∞—Ä–∞', parameters: {type: 'object', properties: {product_id: {type: 'string'}}, required: ['product_id']}}}
];

// –ë–µ–∑–æ–ø–∞—Å–Ω–æ–µ –ø–æ–ª—É—á–µ–Ω–∏–µ –∏—Å—Ç–æ—Ä–∏–∏
let historyFromDB = [];
try {
  const historyNode = $('DB: Get History');
  if (historyNode && historyNode.all) {
    historyFromDB = historyNode.all();
  }
} catch (error) {
  // –ò—Å—Ç–æ—Ä–∏—è –Ω–µ –Ω–∞–π–¥–µ–Ω–∞
}

// –í–°–ï–ì–î–ê –Ω–∞—á–∏–Ω–∞–µ–º —Å system message
const messages = [{ role: 'system', content: systemPrompt }];

// –î–æ–±–∞–≤–ª—è–µ–º –∏—Å—Ç–æ—Ä–∏—é —Ç–æ–ª—å–∫–æ –µ—Å–ª–∏ –æ–Ω–∞ –≤–∞–ª–∏–¥–Ω–∞
if (Array.isArray(historyFromDB) && historyFromDB.length > 0) {
  const sortedHistory = [...historyFromDB];

  for (const msg of sortedHistory) {
    if (msg && msg.json && typeof msg.json.role === 'string' && msg.json.role) {
      const messageObj = {
        role: msg.json.role,
        content: msg.json.content || null
      };

      // –í–ê–ñ–ù–û: –ü–∞—Ä—Å–∏–º tool_calls –∏–∑ JSON-—Å—Ç—Ä–æ–∫–∏ –≤ –æ–±—ä–µ–∫—Ç
      if (msg.json.tool_calls) {
        try {
          messageObj.tool_calls = typeof msg.json.tool_calls === 'string' 
            ? JSON.parse(msg.json.tool_calls) 
            : msg.json.tool_calls;
          messageObj.content = null;
        } catch (e) {
          // –ï—Å–ª–∏ –ø–∞—Ä—Å–∏–Ω–≥ –Ω–µ —É–¥–∞–ª—Å—è, –ø—Ä–æ–ø—É—Å–∫–∞–µ–º tool_calls
        }
      }

      // –î–æ–±–∞–≤–ª—è–µ–º tool_call_id
      if (msg.json.tool_call_id) {
        messageObj.tool_call_id = msg.json.tool_call_id;
      }

      // ‚ö†Ô∏è –í–ê–ñ–ù–û: –ü—Ä–æ–≤–µ—Ä—è–µ–º —á—Ç–æ –¥–ª—è role="tool" –µ—Å—Ç—å tool_call_id
      if (msg.json.role === 'tool' && !msg.json.tool_call_id) {
        // –ü—Ä–æ–ø—É—Å–∫–∞–µ–º —ç—Ç–æ —Å–æ–æ–±—â–µ–Ω–∏–µ - –æ–Ω–æ –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ
        continue;
      }      

      messages.push(messageObj);
    }
  }
}

// –û–ë–Ø–ó–ê–¢–ï–õ–¨–ù–û –¥–æ–±–∞–≤–ª—è–µ–º —Ç–µ–∫—É—â–µ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
messages.push({ 
  role: 'user', 
  content: messageText 
});

// –§–ò–ù–ê–õ–¨–ù–ê–Ø –ü–†–û–í–ï–†–ö–ê
if (messages.length < 2) {
  throw new Error('Messages array must contain at least system and user message');
}

return {
  model: 'gpt-4o-mini', 
  messages: messages,
  tools: tools,
  tool_choice: 'auto',
  _metadata: {
    chat_id: chatId,
    user_id: userId,
    original_message: messageText
  }
};

const toolResult = $input.item.json.result;
const toolCallId = $('Extract Tool Call').first().json.tool_call_id;
const toolName = $('Extract Tool Call').first().json.tool_name;
const toolArgs = $('Extract Tool Call').first().json.tool_args;

// –ü–æ–ª—É—á–∞–µ–º system message –∏ original message
const systemMessage = $('Build OpenAI Request').first().json.messages[0].content;
const originalMessage = $('Build OpenAI Request').first().json._metadata.original_message;

// –ü–æ–ª—É—á–∞–µ–º –∏—Å—Ç–æ—Ä–∏—é –∏–∑ "Build OpenAI Request" (–æ–Ω–∞ —É–∂–µ —Ç–∞–º –µ—Å—Ç—å!)
const allMessages = $('Build OpenAI Request').first().json.messages;

// –°–æ–∑–¥–∞—ë–º –Ω–æ–≤—ã–π –º–∞—Å—Å–∏–≤ —Å–æ–æ–±—â–µ–Ω–∏–π —Å –∏—Å—Ç–æ—Ä–∏–µ–π
const messages = [];

// –ö–æ–ø–∏—Ä—É–µ–º –≤—Å—é —Å—É—â–µ—Å—Ç–≤—É—é—â—É—é –∏—Å—Ç–æ—Ä–∏—é (system + –ø—Ä–µ–¥—ã–¥—É—â–∏–µ —Å–æ–æ–±—â–µ–Ω–∏—è)
for (let i = 0; i < allMessages.length; i++) {
  messages.push(allMessages[i]);
}

// –î–æ–±–∞–≤–ª—è–µ–º —Ç–µ–∫—É—â–∏–π tool call –æ—Ç –∞—Å—Å–∏—Å—Ç–µ–Ω—Ç–∞
messages.push({
  role: "assistant",
  content: null,
  tool_calls: [{
    id: toolCallId,
    type: "function",
    function: {
      name: toolName,
      arguments: toolArgs
    }
  }]
});

// –î–æ–±–∞–≤–ª—è–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç –≤—ã–ø–æ–ª–Ω–µ–Ω–∏—è –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∞
messages.push({
  role: "tool",
  tool_call_id: toolCallId,
  content: JSON.stringify(toolResult)
});

const result = {
  model: "gpt-4o-mini",
  messages: messages
};

return { json: result };

