WITH order_history AS (
  -- –°–æ—Ö—Ä–∞–Ω—è–µ–º –∏—Å—Ç–æ—Ä–∏—é –≤ –∑–∞–∫–∞–∑
  UPDATE orders 
  SET conversation_history = (
    SELECT jsonb_agg(
      jsonb_build_object(
        'role', role,
        'content', content,
        'tool_calls', tool_calls,
        'timestamp', created_at
      ) ORDER BY created_at, sort_order
    )
    FROM conversation_history
    WHERE user_id = {{ $('Extract Message').first().json.user_id }}
  )
  WHERE order_no = '{{ $('DB: Confirm Order').first().json.result }}'
    AND conversation_history IS NULL
  RETURNING id
)
-- –û—á–∏—â–∞–µ–º –∏—Å—Ç–æ—Ä–∏—é –¥–∏–∞–ª–æ–≥–∞
DELETE FROM conversation_history 
WHERE user_id = {{ $('Extract Message').first().json.user_id }};

SELECT 'Order updated and history cleared' as result;

