const output = [];

// –ü–æ–ª—É—á–∞–µ–º –∏–∑ –ø—Ä–µ–¥—ã–¥—É—â–∏—Ö –Ω–æ–¥
const chatId = $('Build OpenAI Request').first().json._metadata.chat_id;
const userId = $('Build OpenAI Request').first().json._metadata.user_id;
const userMessage = $('Build OpenAI Request').first().json._metadata.original_message;

// –ü–æ–ª—É—á–∞–µ–º –æ—Ç–≤–µ—Ç –∞—Å—Å–∏—Å—Ç–µ–Ω—Ç–∞ –∏–∑ OpenAI
const assistantResponse = $('Call OpenAI').first().json.choices[0].message.content || '';

// –°–º–æ—Ç—Ä–∏–º —Å—Ç–∞—Ç—É—Å –æ—Ç–ø—Ä–∞–≤–∫–∏ —Ñ–æ—Ç–æ –∏–∑ —Ç–µ–∫—É—â–µ–π –Ω–æ–¥—ã
const currentItem = items[0].json;

// –ò–°–ü–†–ê–í–õ–ï–ù–ò–ï: –ø—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ –º–∞—Å—Å–∏–≤–∞ photo –≤ result (–æ—Ç–≤–µ—Ç –æ—Ç Telegram API)
const photoSent = !!(
  currentItem.result?.photo && 
  Array.isArray(currentItem.result.photo) && 
  currentItem.result.photo.length > 0
);

// –§–æ—Ä–º–∏—Ä—É–µ–º –∑–∞–ø–∏—Å—å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
output.push({
  user_id: userId,
  chat_id: chatId,
  role: 'user',
  content: userMessage,
  tool_calls: null,
  tool_call_id: null,
  sort_order: 1
});

// –§–æ—Ä–º–∏—Ä—É–µ–º –æ—Ç–≤–µ—Ç –∞—Å—Å–∏—Å—Ç–µ–Ω—Ç–∞
output.push({
  user_id: userId,
  chat_id: chatId,
  role: 'assistant',
  content: photoSent ? '–§–æ—Ç–æ –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–æ' : (assistantResponse || currentItem.result?.text || '–§–æ—Ç–æ –Ω–µ –Ω–∞–π–¥–µ–Ω–æ'),
  tool_calls: null,
  tool_call_id: null,
  sort_order: 2
});

return output.map(i => ({ json: i }));
